package com.rxyzent.remainder.core.utilsimport android.graphics.Canvasimport android.graphics.Rectimport android.view.Viewimport androidx.recyclerview.widget.RecyclerViewclass VerticalItemDecoration(val margin: Int) : RecyclerView.ItemDecoration() {    override fun getItemOffsets(outRect: Rect, view: View, parent: RecyclerView, state: RecyclerView.State) {        val total=state.itemCount        val current=parent.getChildAdapterPosition(view)        outRect.bottom = margin / 2        outRect.top = margin / 2        if (current == 0) {            outRect.top = margin            outRect.bottom = margin/2        }        else if (current == total - 1) {            outRect.top=margin/2            outRect.bottom = margin        }//        outRect.left=margin//        outRect.right=margin    }    override fun onDrawOver(c: Canvas, parent: RecyclerView, state: RecyclerView.State) {//    c.drawColor()    }}